// overwrite the standard one, 
//this goes at the very top of the events list

const prefixes = globalVars("prefixes");

Bot.checkTag = function(content) {
	const separator = Files.data.settings.separator || '\\s+';
	content = content.split(new RegExp(separator))[0];     

	for (let i = 0; i < prefixes.length; i++) { 
		const pre = prefixes[i];
		if(content.startsWith(pre)){
			return content.substring(pre.length);
		}
	}

	return  null;
};
